        dpzbyi = -dpzba*abar*abar+dpzbz*abar*(zion(i)-zbar)
        a1  = dtdp*( (velx*velx-dpd_row(1))*ddtby(i)
     1               -dpa_row(1)*datby(i)-dpz_row(1)*dztby(i) 
     2              +(-dpdbyi)*ddt
     3              -dpabyi*dat-dpzbyi*dzt )
     4        - zwork1(itemp)*dtdp*dptbyi
