      double precision temp,den,fr,dfrdt,dfrdd,rr,drrdt,drrdd

c..locals
      double precision term,dtermdt,rev,drevdt,aa,daa,bb,dbb,cc,dcc,
     1                 dd,ddd,ee,dee,gt9x,dgt9x,t9a,dt9a,t9a13,dt9a13,
     2                 t9a56,dt9a56,zz


c..mg25(a,g)si29
      aa    = 1.0d0 + 0.0630*t9
      zz    = 1.0d0/aa

      t9a   = t9*zz
      dt9a  = (1.0d0 - t9a*0.0630)*zz

